mapper.update:
	UPDATE ERP_WAREHOUSE 
	
		
      WARE_NUM =WARE_NUM - #{int,jdbcType=INTEGER},
    
	
	where WARE_NUM>0 AND raw_id=#{rawId}

mapper.select:
SELECT war.*,kinds.KIN_NAME kinName,kinds.KIN_PRICE kinPrice FROM erp_warehouse war ,erp_kinds kinds WHERE war.KIN_ID=kinds.KIN_ID AND
( kinds.KIN_Name like CONCAT('%',#{prie},'%' ) ) and
war.ISVA=1 ORDER BY war.CREATETIME DESC LIMIT #{page},#{limit}

SELECT war.*,raw.RAW_NAME rawName,raw.RAW_PRICE rawPrice FROM erp_warehouse war ,erp_raw raw WHERE war.RAW_ID=raw.RAW_ID AND
( raw.RAW_NAME like CONCAT('%',#{priea},'%' ) ) and
war.ISVA=1 ORDER BY war.CREATETIME DESC LIMIT #{page},#{limit}
